content.query:
let totalRequests = requests | where timestamp > ago(1h) | count;
let failedRequests = requests | where timestamp > ago(1h) | where success == false | count;
let avgDuration = requests | where timestamp > ago(1h) | summarize avg(duration);
let p95Duration = requests | where timestamp > ago(1h) | summarize percentile(duration, 95);
datatable(Metric:string, Value:string)
[
    "Total Requests (1h)", tostring(toscalar(totalRequests)),
    "Failed Requests (1h)", tostring(toscalar(failedRequests)),
    "Average Duration (ms)", tostring(round(toscalar(avgDuration), 2)),
    "P95 Duration (ms)", tostring(round(toscalar(p95Duration), 2))
]
